function converterParaCSV(dados) {
  const cabecalho = Object.keys(dados[0]).join(",")
  const linhas = dados.map(obj =&gt; 
    Object.values(obj).map(v =&gt; 
      `"${String(v).replace(/"/g, '""')}"`
    ).join(",")
  )
  return [cabecalho, ...linhas].join("\n")
}
